import {Component, OnInit} from '@angular/core';
import {TariffService} from "../shared/services/tariff.service";
import {Tariff} from "../shared/models/tarifs";

@Component({
  selector: ' app-tariff-container',
  templateUrl: './tariff-container.component.html',
  styleUrls: ['./tariff-container.component.sass']
})
export class TariffContainerComponent implements OnInit{
  tariffs: Tariff[] = [];
  sortDirection: 'asc' | 'desc' = 'asc';
  selectedProperty: keyof typeof this.tariffs[0] | null = null;
  constructor(private resultService: TariffService) {
  }
  ngOnInit() {
    this.getTariffs();
  }

  getTariffs() {
    this.resultService.getTariffs().subscribe({
      next: res => {
        this.tariffs = res;
      },
      error: error => {
        // TODO ERROR HANDLING?
        console.log('error', error);
      }
    });
  }
  toggleArrow(property: string): string {
    if (this.selectedProperty === property) {
      return this.sortDirection === 'asc' ? '&#x25B4;' : '&#x25BE;';
    }
    return '&#x25B4;';
  }
}
<div class="outer-wrapper">
  <div class="table">
    <div class="table-header">
      <div class="table-header-cell" appSort [tariffs]="tariffs" [property]="'id'"
           (propertyChange)="selectedProperty = $event" (directionChange)="sortDirection = $event">
        Id <span class="arrow" [innerHTML]="toggleArrow('id')"></span>
      </div>
      <div class="table-header-cell" appSort [tariffs]="tariffs" [property]="'name'"
           (propertyChange)="selectedProperty = $event" (directionChange)="sortDirection = $event">
        Name <span class="arrow" [innerHTML]="toggleArrow('name')"></span>
      </div>
      <div>
        <span class="table-header-cell" appSort [tariffs]="tariffs" [property]="'downloadSpeed'"
              (propertyChange)="selectedProperty = $event" (directionChange)="sortDirection = $event">
         Download<span class="arrow" [innerHTML]="toggleArrow('downloadSpeed')"></span> /
        </span>
        <span class="table-header-cell" appSort [tariffs]="tariffs" [property]="'uploadSpeed'"
              (propertyChange)="selectedProperty = $event" (directionChange)="sortDirection = $event">
         Upload<span class="arrow" [innerHTML]="toggleArrow('uploadSpeed')"></span>
        </span>
      </div>
      <div class="table-header-cell tariff-benefits-header">
        Tariff Benefits
      </div>
      <div class="table-header-cell" appSort [tariffs]="tariffs" [property]="'price'"
           (propertyChange)="selectedProperty = $event" (directionChange)="sortDirection = $event">
        Price<span class="arrow" [innerHTML]="toggleArrow('price')"></span>
      </div>
    </div>
    <app-tariff-list [tariffs]="tariffs"></app-tariff-list>
  </div>
</div>
